    def __init__(self):
        self.twilio_client = None
        if TWILIO_AVAILABLE:
            sid = os.environ.get('TWILIO_ACCOUNT_SID')
            token = os.environ.get('TWILIO_AUTH_TOKEN')
            if sid and token:
                self.twilio_client = Client(sid, token)
        
        self.whatsapp_from = os.environ.get('TWILIO_WHATSAPP_FROM', 'whatsapp:+14155238886')
    
    def send_message(self, phone: str, message: str) -> Dict[str, Any]:
        """Send WhatsApp message to patient."""
        if not self.twilio_client:
            return {'success': False, 'error': 'Twilio not configured'}
        
        try:
            # Format phone number
            phone = phone.strip()
            if not phone.startswith('+'):
                phone = '+91' + phone  # Default to India
            
            msg = self.twilio_client.messages.create(
                from_=self.whatsapp_from,
                body=message,
                to=f'whatsapp:{phone}'
            )
            return {'success': True, 'message_sid': msg.sid}
        except Exception as e:
            return {'success': False, 'error': str(e)}
    
